'use strict';
const {
  Model
} = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  class Product extends Model {
    /**
     * Helper method for defining associations.
     * This method is not a part of Sequelize lifecycle.
     * The `models/index` file will call this method automatically.
     */
    static associate(models) {

      this.belongsTo(models.Category, {
        foreignKey: {
          name: 'categoryId', 
          allowNull: true}, 
          onDelete: 'CASCADE', 
          as: 'Categories' 
      })

      this.belongsTo(models.FirstClass, {
        foreignKey: {
          name: 'classOneId', 
          allowNull: true}, 
          onDelete: 'CASCADE', 
          as: 'FirstClasses' 
      })

      this.belongsTo(models.SeClass, {
        foreignKey: {
          name: 'classOneId', 
          allowNull: true}, 
          onDelete: 'CASCADE', 
          as: 'FirstClasses' 
      })

    }
  }
  Product.init({
    productId: {
      type: DataTypes.UUID,
      primaryKey: true,
      defaultValue: DataTypes.UUIDV4,
      allowNull: false
    },
    categoryId: {
      allowNull: true,
      type: DataTypes.UUID,
      defaultValue: DataTypes.UUIDV4
    },
    productName: {
      type: DataTypes.STRING
    },
    price: {
      type: DataTypes.STRING
    },
    createdAt: {
      allowNull: false,
      type: DataTypes.DATE
    },
    updatedAt: {
      allowNull: false,
      type: DataTypes.DATE
    }
  }, {
    sequelize,
    modelName: 'Product',
  });
  return Product;
};

