sourcesContent:
import model from '../database/models';

const productRoutes = model.Product;

const createProduct = async (req, res) => {
    const {productName, price, categoryId} = req.body;
    if(!productName && !price && !categoryId){
        res.send({
            message: "Please make sure you include both product and price!"
        })
    } 
    await productRoutes.findOrCreate({
        where: {
            productName: req.body.productName,
            price: req.body.price,
            categoryId: req.body.categoryId,
        }
    })
    .then((data) => {
        if(data[1]){
            res.status(201).send({
                message: "successfully created!",
                data
            })
        } else {
            res.send({
                message: "This product already exists!"
            })
        }
    })
    .catch((err) => {
        res.status(500).send(err)
    })
}

const getProduct = async (req, res) => {
    await productRoutes.findAll({
        order: [["productName", "ASC"]],
    })
    .then((data) => {
        res.status(200).send({
            message: "List of all fruits available!",
            data
        })
    })
    .catch((err) => {
        res.send(err)
    })
}

const updateProduct = async (req, res) => {

    const id = req.params.id;

    await productRoutes.update(req.body,
        {
        where: {
            productName: id
        }
    })
    .then((data => {
        if(data == 1){
            res.send({
                message: "Updated product successfully!",
                body: {data}
            })
        } else {
            res.send({
                message: `Cannot update product ${id}!`,
                data
            })
        }
    }))
    .catch((err) => {
        res.send({
            message: "Product updated successfully!"
        });
        console.log(err)
    })
}

const deleteOneProduct = async (req, res) => {

    const id = req.params.id;

    await productRoutes.destroy({
        where: {
            productName: id
        }
    })
    .then((data => {
        res.send({
            message: "Deleted product successfully!",
            data
        })
    }))
    .catch((err) => {
        res.send({
            message: "error!"
        })
    })
}

const deleteProduct = async (req, res) => {
    await productRoutes.destroy({
        where: {}
    })
    .then((data => {
        res.send({
            message: "Deleted product successfully!",
            data
        })
    }))
}

export  {
    createProduct,
    getProduct,
    updateProduct,
    deleteOneProduct,
    deleteProduct
}

