sourcesContent:
'use strict';
const {
  Model
} = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  class User extends Model {
    static associate(models) {

      this.belongsTo(models.Category, {
        foreignKey: 'userId', 
        as: 'Categories', 
        onDelete: 'SET NULL' 
      })

      this.belongsTo(models.Role, {
        foreignKey: 'roleId', 
        as: 'Roles', 
        onDelete: 'SET DEFAULT',
      })

    }
  }
  User.init({
    userId: {
      type: DataTypes.UUID,
      primaryKey: true,
      defaultValue: DataTypes.UUIDV4,
      allowNull: false
    },
    //All users are predefined as clients during registration, we protest the route for updating users because it is here that the admin, managers and workers are created. The route for creating/registering a user is not protected for the sake of allowing clients to sign up.
    roleId: {
      allowNull: true,
      type: DataTypes.INTEGER,
      defaultValue: 4, // default role is client
    },
    categoryId: {
      allowNull: true,
      type: DataTypes.UUID,
    },
    firstName: {
      type: DataTypes.STRING,
      allowNull: false,
    },
    lastName: {
      type: DataTypes.STRING,
      allowNull: false,
    },
    email: {
      type: DataTypes.STRING,
      allowNull: false,
      validate: {
        isEmail: true
      }
    },
    password: {
      type: DataTypes.STRING,
      allowNull: false,
      validate: { 
        min: 8 
      },
    },
    createdAt: {
      allowNull: false,
      type: DataTypes.DATE,
    },
    updatedAt: {
      allowNull: false,
      type: DataTypes.DATE,
    },
  }, {
    sequelize,
    modelName: 'User',
    tableName: 'Users'
  });
  return User;
};

