'use strict';
const {
  Model
} = require('sequelize');
module.exports = (sequelize, DataTypes) => {
  class Address extends Model {
    static associate(models) {

      this.belongsTo(models.Profile, {
        foreignKey: {
          name: 'profileId', 
          allowNull: true
        }, 
        onDelete: 'CASCADE', 
        as: 'Profiles' 
      })
    }
  }
  Address.init({
    addressId: {
      type: DataTypes.UUID,
      primaryKey: true,
      defaultValue: DataTypes.UUIDV4,
      allowNull: false 
    },
    country: {
      type: DataTypes.STRING
    },
    country: {
      type: DataTypes.STRING
    },
    province: {
      type: DataTypes.STRING
    },
    district: {
      type: DataTypes.STRING
    },
    sector: {
      type: DataTypes.STRING
    },
    street: {
      type: DataTypes.STRING
    },

  }, {
    sequelize,
    modelName: 'Address',
    tableName: 'Addresses'
  });
  return Address;
};

